var page = $('.page'),
	pageWidth = page.width(),
	popup = {
		activeClass: 'open',
		open: function (popupName) {
			page.addClass('lock').css({ 'margin-right': page.width() - pageWidth });
			document.querySelector('[data-popup="' + popupName + '"]').classList.add(this.activeClass);
		},
		openByHash: function () {
			if (location.hash != '' && location.hash.indexOf('#popup/') >= 0) {
				this.open(location.hash.replace('#popup/', ''));
			}
		},
		close: function () {
			page.removeClass('lock').css({ 'margin-right': '' });
			$('.popup').removeClass(this.activeClass);
			history.replaceState('', '', location.pathname);
		}
	};

window.addEventListener('load', function (e) {
	popup.openByHash();
});
window.addEventListener('hashchange', function (e) {
	popup.openByHash();
});

$('.popup__close,.popup__overlay').click(function () {
	popup.close();
});

$('.select').click(function(){
	$('.select').not(this).removeClass('open');
	$(this).toggleClass('open');
	if( $(this).hasClass('open') ){
		$(this).find('.select__item').click(function(){
			if( !$(this).hasClass('selected') ){
				addSelect(this);
			}
		});
	}
	return false;
});
$(document).click(function(){
	$('.select.open').removeClass('open');
});
function addSelect(elem){
	$(elem).addClass('selected').siblings().removeClass('selected');
	var value = $(elem).html();
	$(elem).parents('.select').find('.select__selected').html(value).addClass('selected');
	$(elem).parents('.select').find('.select__input').attr('value',value).change();
}
$('.slidetoggle').click(function(){
	$(this).removeAttr('hidden').toggleClass('active').next().slideToggle();
});
$('.textmore__toggle').click(function(){
	var texmore = $(this).parents('.textmore'),
		display = console.log( $(this).css('display') );
	texmore.find('.textmore__toggle').toggleClass('active');
	if ( display === 'block' ){
		texmore.find('.textmore__hidden').slideToggle();
	}else{
		texmore.find('.textmore__hidden').toggle();
	}
	return false;
});

